SELECT CO.CourseOfferId, CO.CourseId, CO.Year, CO.Quartile, C.CourseName, C.CourseDescription, C.DegreeId, C.ECTS, D.Dept, D.DegreeDescription, D.TotalECTS, TATC.TeacherId, T.TeacherName, T.Address, T.BirthyearTeacher, T.Gender FROM Courses C, CourseOffers CO, Degrees D, TeacherAssignmentsToCourses TATC, Teachers T WHERE C.CourseId = CO.CourseId and CO.CourseOfferId = 1 and C.DegreeId = D.DegreeId and T.TeacherId = TATC.TeacherId and TATC.CourseOfferId = CO.CourseOfferId;
SELECT CO.CourseOfferId, CO.CourseId, CO.Year, CO.Quartile, S.StudentId, S.StudentName, S.Address, S.BirthyearStudent, S.Gender, D.DegreeId, D.Dept, D.DegreeDescription, D.TotalECTS FROM StudentAssistants SA, CourseOffers CO, Students S, StudentRegistrationsToDegrees SRTD, Degrees D WHERE SA.StudentRegistrationId = 3 and SA.StudentRegistrationId = SRTD.StudentRegistrationId and SA.CourseOfferId = CO.CourseOfferId and D.DegreeId = SRTD.DegreeId and SRTD.Studentid = S.StudentId;
SELECT AVG(CReg.grade) FROM CourseRegistrations CReg WHERE CReg.StudentRegistrationId = 3;
